import React, {Component} from "react";
import ReactDOM from "react-dom";
import "./index.css";

class Quiz extends Component {

	constructor() {
		super();
		this.state = {
		  question: {},
		  pending: 0 
		};
	  }

	getQuestion = () => {
		fetch('http://localhost:8080/admin/questions', {mode: 'cors'})
			.then(response => response.json())
			.then(data => this.setState({question: 
				{questionText: data.fact, 
				answerOptions: data.options.map((opt) => { return {answerText: opt, isCorrect: (opt === data.answer) } ; })}
			}))
	};

	getPending= () => {
		fetch('http://localhost:8080/facts/pending', {mode: 'cors'})
			.then(response => response.json())
			.then(data => this.setState({question: this.state.question, pending: data}))
	};

	// componentDidMount function to get question
	componentDidMount() {
		this.getQuestion();
		this.getPending();
		console.log(this.state)
	}

	handleAnswerOptionClick = (isCorrect) => {
		alert("ok");
		/*if (isCorrect) {
			setScore(score + 1);
		}

		const nextQuestion = currentQuestion + 1;
		if (nextQuestion < questions.length) {
			setCurrentQuestion(nextQuestion);
		} else {
			setShowScore(true);
		}*/
	};

	render() {
		return (
			<div className='app'>
				(
					<>
						<div className='question-section'>
							<div className='question-count'>
								<span>Questions: {this.state.pending}</span>/{this.state.pending}
							</div>
							<div className='question-text'>{this.state.question.questionText}</div>
						</div>
						<div className='answer-section'>
							{this.state.question.answerOptions.map((answerOption) => (
								<button onClick={() => this.handleAnswerOptionClick(answerOption.isCorrect)}>{answerOption.answerText}</button>
							))}
						</div>
					</>
				)}
			</div>
		)
	};
}

ReactDOM.render(<Quiz/>, document.getElementById("root"));
